import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import './Components/Calculator.css';
import Frame from './Components/Frame';
import axios from 'axios';
import Pin from './Components/Pin'

class App extends Component {
  state = {
    frames:[],
    pins:10,
    newFrame:true
  }
  componentDidMount(){
    axios.get('http://localhost:5000/api/bowling/get')
    .then((response)=>{
      console.log(response)
      this.setState({
        frames: response.data,

      })
    })

  }
  knockHandler =(val:any)=>{
    console.log(10-Number(val))
    let newF = this.state.newFrame;

      // this.setState({

      //  pins:newF ?10-Number(val):10,
      //  newFrame:!newF
      //   })
    axios.post('http://localhost:5000/api/bowling/post',{"pins": val})
    .then((response)=>{
      console.log(response)
      this.setState({
        frames: response.data,

      })
     })

  }
  render(){
    let frames = this.state.frames;
    let pins = this.state.pins;
    return (
      <div className="App">
         <div className="pins">
          <p>Knock down pins</p>
          {[...Array(pins+1)].map((e, i) => <Pin click={this.knockHandler.bind(this,i)} pin_num={i}/>)}
          </div>
          <div className="game-table">
         {frames.map((frame : any) =>      
                    <Frame 
                    position={frame.position}
                    shotOne={frame.firstShotScore=="-1"?"":frame.firstShotScore}
                    shotTwo={frame.secondShotScore=="-1"?"":frame.secondShotScore}
                    frame_total={frame.frameScore}
                    />

                    )}
      </div>
      {/* <button className="test-button" onClick={this.knockHandler.bind(this,2)} value="4">Click men</button> */}
      </div>
    );
  }
}

export default App;
